import React, { useState } from 'react';
import APIClient from '../api/api';
import './LogForm.css';

function LogForm() {
  const [timestamp, setTimestamp]         = useState('');
  const [userId, setUserId]               = useState('');
  const [actionType, setActionType]       = useState('');
  const [resource, setResource]           = useState('');
  const [location, setLocation]           = useState('US');
  const [device, setDevice]               = useState('laptop');
  const [hourOfDay, setHourOfDay]         = useState('0');
  const [numFilesAccessed, setNumFiles]   = useState('');
  const [result, setResult]               = useState(null);
  const [error, setError]                 = useState(null);

  const handleSubmit = async function(e) {
    e.preventDefault();
    setError(null);
    const log = {
      timestamp: timestamp,
      user_id: Number(userId),
      action_type: actionType,
      resource: resource,
      location: location,
      device: device,
      hour_of_day: Number(hourOfDay),
      num_files_accessed: Number(numFilesAccessed)
    };
    try {
      const res = await APIClient.post('/detect-anomaly', log);
      setResult(res.data);
    } catch (err) {
      setError('Failed to analyze log.');
      console.error(err);
    }
  };

  return React.createElement(
    'div',
    { className: 'log-form-container' },
    React.createElement('h2', null, 'Analyze Single Log'),
    React.createElement(
      'form',
      { className: 'log-form', onSubmit: handleSubmit },
      React.createElement('label', null, 'Timestamp:'),
      React.createElement('input', {
        type: 'datetime-local',
        value: timestamp,
        onChange: function(e) { setTimestamp(e.target.value); },
        required: true
      }),
      React.createElement('label', null, 'User ID:'),
      React.createElement('input', {
        type: 'number',
        value: userId,
        onChange: function(e) { setUserId(e.target.value); },
        required: true
      }),
      React.createElement('label', null, 'Action Type:'),
      React.createElement('input', {
        type: 'text',
        value: actionType,
        onChange: function(e) { setActionType(e.target.value); },
        required: true
      }),
      React.createElement('label', null, 'Resource:'),
      React.createElement('input', {
        type: 'text',
        value: resource,
        onChange: function(e) { setResource(e.target.value); },
        required: true
      }),
      React.createElement('label', null, 'Location:'),
      React.createElement('select', {
        value: location,
        onChange: function(e) { setLocation(e.target.value); }
      },
        React.createElement('option', { value: 'US' }, 'US'),
        React.createElement('option', { value: 'IN' }, 'IN'),
        React.createElement('option', { value: 'UK' }, 'UK')
      ),
      React.createElement('label', null, 'Device:'),
      React.createElement('select', {
        value: device,
        onChange: function(e) { setDevice(e.target.value); }
      },
        React.createElement('option', { value: 'laptop' }, 'laptop'),
        React.createElement('option', { value: 'mobile' }, 'mobile'),
        React.createElement('option', { value: 'vpn' }, 'vpn')
      ),
      React.createElement('label', null, 'Hour of Day:'),
      React.createElement('input', {
        type: 'number',
        min: 0,
        max: 23,
        value: hourOfDay,
        onChange: function(e) { setHourOfDay(e.target.value); },
        required: true
      }),
      React.createElement('label', null, 'Files Accessed:'),
      React.createElement('input', {
        type: 'number',
        value: numFilesAccessed,
        onChange: function(e) { setNumFiles(e.target.value); },
        required: true
      }),
      React.createElement('button', { type: 'submit' }, 'Check Anomaly')
    ),
    error && React.createElement('p', { className: 'error' }, error),
    result && React.createElement(
      'div',
      { className: 'result' },
      React.createElement('p', null, 'Score: ' + result.anomaly_score.toFixed(4)),
      React.createElement(
        'span',
        { className: result.is_anomalous ? 'badge anomalous' : 'badge normal' },
        result.is_anomalous ? 'Anomalous' : 'Normal'
      ),
      React.createElement('pre', null, JSON.stringify(result, null, 2))
    )
  );
}

export default LogForm;
